package com.example.capstone2.adapter

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.TextView
 import androidx.core.graphics.toColorInt
import androidx.recyclerview.widget.DiffUtil
import androidx.recyclerview.widget.ListAdapter
import androidx.recyclerview.widget.RecyclerView
import com.example.capstone2.R
import com.example.capstone2.data.models.Conversation

class ConversationsAdapter(private val onClick: (Conversation) -> Unit) : ListAdapter<Conversation, ConversationsAdapter.VH>(Diff()) {

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): VH {
        val v = LayoutInflater.from(parent.context).inflate(R.layout.item_conversation, parent, false)
        return VH(v, onClick)
    }

    override fun onBindViewHolder(holder: VH, position: Int) {
        holder.bind(getItem(position))
    }

    class VH(itemView: View, val onClick: (Conversation) -> Unit) : RecyclerView.ViewHolder(itemView) {
        private val tvPartner: TextView? = itemView.findViewById(R.id.tvPartner)
        private val tvLast: TextView? = itemView.findViewById(R.id.tvLastMessage)
        private val tvTime: TextView? = itemView.findViewById(R.id.tvTime)
        private val tvUnread: TextView? = itemView.findViewById(R.id.tvUnread)

        fun bind(c: Conversation) {
            tvPartner?.text = c.partnerName ?: "User ${c.partnerID}"
            tvLast?.text = c.lastMessage ?: ""
            tvTime?.text = c.lastMessageAt ?: ""
            if (c.unreadCount > 0) {
                tvUnread?.visibility = View.VISIBLE
                tvUnread?.text = c.unreadCount.toString()
                // make preview text darker when there are unread messages
                tvLast?.setTextColor("#212121".toColorInt())
            } else {
                tvUnread?.visibility = View.GONE
                tvLast?.setTextColor("#757575".toColorInt())
            }

            itemView.setOnClickListener { onClick(c) }
        }
    }

    class Diff : DiffUtil.ItemCallback<Conversation>() {
        override fun areItemsTheSame(oldItem: Conversation, newItem: Conversation): Boolean = oldItem.conversationID == newItem.conversationID
        override fun areContentsTheSame(oldItem: Conversation, newItem: Conversation): Boolean = oldItem == newItem
    }
}

package com.example.capstone2.adapter

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.TextView
import androidx.core.graphics.toColorInt
import androidx.recyclerview.widget.DiffUtil
import androidx.recyclerview.widget.ListAdapter
import androidx.recyclerview.widget.RecyclerView
import com.example.capstone2.R
import com.example.capstone2.data.models.Conversation

class ConversationsAdapter(private val onClick: (Conversation) -> Unit) : ListAdapter<Conversation, ConversationsAdapter.VH>(Diff()) {

    // Ensure adapter never shows duplicate partner rows: dedupe by partnerID preserving order
    override fun submitList(list: List<Conversation>?) {
        if (list == null) {
            super.submitList(null)
            return
        }
        val seen = mutableSetOf<Long>()
        val deduped = mutableListOf<Conversation>()
        for (c in list) {
            if (seen.add(c.partnerID)) deduped.add(c)
        }
        super.submitList(deduped)
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): VH {
        val v = LayoutInflater.from(parent.context).inflate(R.layout.item_conversation, parent, false)
        return VH(v, onClick)
    }

    override fun onBindViewHolder(holder: VH, position: Int) {
        holder.bind(getItem(position))
    }

    class VH(itemView: View, val onClick: (Conversation) -> Unit) : RecyclerView.ViewHolder(itemView) {
        private val tvPartner: TextView? = itemView.findViewById(R.id.tvPartner)
        private val tvLast: TextView? = itemView.findViewById(R.id.tvLastMessage)
        private val tvTime: TextView? = itemView.findViewById(R.id.tvTime)
        private val tvUnread: TextView? = itemView.findViewById(R.id.tvUnread)

        fun bind(c: Conversation) {
            // Normal partner display
            tvPartner?.text = c.partnerName ?: "User ${c.partnerID}"
            // Preview text should be just the last message
            tvLast?.text = c.lastMessage ?: ""

            // Try to parse common timestamp formats and format to a short human-friendly form
            var displayTime = c.lastMessageAt ?: ""
            try {
                val s = c.lastMessageAt
                if (!s.isNullOrBlank()) {
                    try {
                        val fmtIn = java.time.format.DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss")
                        val ldt = java.time.LocalDateTime.parse(s, fmtIn)
                        val fmtOut = java.time.format.DateTimeFormatter.ofPattern("HH:mm")
                        displayTime = ldt.format(fmtOut)
                    } catch (_: Exception) {
                        // fallback: try ISO
                        try {
                            val instant = java.time.Instant.parse(s)
                            val zdt = instant.atZone(java.time.ZoneId.systemDefault())
                            val fmtOut2 = java.time.format.DateTimeFormatter.ofPattern("HH:mm")
                            displayTime = zdt.format(fmtOut2)
                        } catch (_: Exception) {
                            // leave raw
                            displayTime = s
                        }
                    }
                }
            } catch (_: Exception) {
                displayTime = c.lastMessageAt ?: ""
            }

            tvTime?.text = displayTime

            // By default make preview text readable/darker; when unread show a stronger emphasis
            tvLast?.setTextColor("#212121".toColorInt())
            if (c.unreadCount > 0) {
                tvUnread?.visibility = View.VISIBLE
                tvUnread?.text = c.unreadCount.toString()
                // emphasize preview text when there are unread messages
                tvLast?.setTextColor("#000000".toColorInt())
            } else {
                tvUnread?.visibility = View.GONE
            }

            itemView.setOnClickListener { onClick(c) }
        }
    }

    class Diff : DiffUtil.ItemCallback<Conversation>() {
        override fun areItemsTheSame(oldItem: Conversation, newItem: Conversation): Boolean = oldItem.conversationID == newItem.conversationID
        override fun areContentsTheSame(oldItem: Conversation, newItem: Conversation): Boolean = oldItem == newItem
    }
}

package com.example.capstone2.customer

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.EditText
import android.widget.ImageButton
import android.widget.TextView
import android.widget.Toast
import androidx.fragment.app.Fragment
import androidx.lifecycle.ViewModelProvider
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.capstone2.R
import com.example.capstone2.adapter.ChatAdapter
import com.example.capstone2.repository.SharedPrefManager
import com.example.capstone2.viewmodel.ChatViewModel
import com.example.capstone2.viewmodel.ChatViewModelFactory

class ChatFragment : Fragment() {

    private lateinit var rvMessages: RecyclerView
    private lateinit var etMessage: EditText
    private lateinit var btnSend: ImageButton
    private lateinit var adapter: ChatAdapter
    private lateinit var viewModel: ChatViewModel
    private lateinit var tvChatTitle: TextView

    private var otherUserId: Long = -1L
    private var conversationID: String? = null
    private var currentUserId: Long = -1L
    private var otherName: String? = null

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        arguments?.let {
            if (it.containsKey("otherUserID")) otherUserId = it.getLong("otherUserID", -1L)
            if (it.containsKey("conversationID")) conversationID = it.getString("conversationID")
            if (it.containsKey("otherName")) otherName = it.getString("otherName")
        }

        currentUserId = SharedPrefManager.getUserId(requireContext()) ?: -1L
    }

    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? {
        val v = inflater.inflate(R.layout.fragment_chat, container, false)
        tvChatTitle = v.findViewById(R.id.tvChatTitle)
        rvMessages = v.findViewById(R.id.rvMessages)
        etMessage = v.findViewById(R.id.etMessage)
        btnSend = v.findViewById(R.id.btnSend)

        otherName?.let { tvChatTitle.text = it; tvChatTitle.visibility = View.VISIBLE }

        adapter = ChatAdapter(currentUserId)
        rvMessages.setHasFixedSize(true)
        rvMessages.layoutManager = LinearLayoutManager(requireContext()).apply { stackFromEnd = true }
        rvMessages.adapter = adapter
        rvMessages.isNestedScrollingEnabled = true
        rvMessages.clipToPadding = false
        try { rvMessages.elevation = 8f * resources.displayMetrics.density } catch (_: Exception) {}

        // Adjust RecyclerView bottom padding so last message is visible above the input (post to ensure measurements are ready)
        try {
            v.post {
                try {
                    val input = v.findViewById<View?>(R.id.layoutInput)
                    val inputH = input?.height ?: (56 * resources.displayMetrics.density).toInt()
                    val extra = (8 * resources.displayMetrics.density).toInt()
                    rvMessages.setPadding(rvMessages.paddingLeft, rvMessages.paddingTop, rvMessages.paddingRight, inputH + extra)
                } catch (_: Exception) {}
            }
        } catch (_: Exception) {}

        val factory = ChatViewModelFactory(requireContext(), currentUserId)
        viewModel = ViewModelProvider(this, factory).get(ChatViewModel::class.java)

        viewModel.messages.observe(viewLifecycleOwner) { list ->
            val snapshot = list?.toList() ?: emptyList()
            val enriched = snapshot.map { msg ->
                if (msg.senderName != null) return@map msg
                val inferredName = when (msg.senderID) {
                    currentUserId -> SharedPrefManager.getUserFullName(requireContext()) ?: "You"
                    otherUserId -> otherName
                    else -> null
                }
                if (inferredName != null) msg.copy(senderName = inferredName) else msg
            }
            adapter.submitList(enriched)
            rvMessages.visibility = View.VISIBLE
            try { rvMessages.bringToFront() } catch (_: Exception) {}
            rvMessages.post { if (enriched.isNotEmpty()) try { rvMessages.scrollToPosition(enriched.size - 1) } catch (_: Exception) {} }
        }

        viewModel.error.observe(viewLifecycleOwner) { err -> err?.let { Toast.makeText(requireContext(), it, Toast.LENGTH_SHORT).show() } }

        if (conversationID != null) {
            Toast.makeText(requireContext(), "Loading conversation...", Toast.LENGTH_SHORT).show()
            viewModel.loadConversation(conversationID = conversationID)
        } else if (otherUserId != -1L) {
            Toast.makeText(requireContext(), "Loading conversation...", Toast.LENGTH_SHORT).show()
            viewModel.loadConversation(otherUserID = otherUserId)
        }

        btnSend.setOnClickListener {
            val text = etMessage.text.toString().trim()
            if (text.isEmpty()) return@setOnClickListener
            if (otherUserId == -1L) {
                Toast.makeText(requireContext(), "No recipient specified", Toast.LENGTH_SHORT).show()
                return@setOnClickListener
            }
            viewModel.sendMessage(receiverID = otherUserId, text = text, conversationID = conversationID)
            etMessage.setText("")
        }

        return v
    }

    companion object {
        @JvmStatic
        fun newInstance(otherUserID: Long, conversationID: String? = null, otherName: String? = null) = ChatFragment().apply {
            arguments = Bundle().apply {
                putLong("otherUserID", otherUserID)
                conversationID?.let { putString("conversationID", it) }
                otherName?.let { putString("otherName", it) }
            }
        }
    }
}

package com.example.capstone2.customer

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.EditText
import android.widget.ImageButton
import android.widget.TextView
import android.widget.Toast
import androidx.fragment.app.Fragment
import androidx.lifecycle.ViewModelProvider
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.capstone2.R
import com.example.capstone2.adapter.ChatAdapter
import com.example.capstone2.repository.SharedPrefManager
import com.example.capstone2.viewmodel.ChatViewModel
import com.example.capstone2.viewmodel.ChatViewModelFactory
import android.util.Log

class ChatFragment : Fragment() {
    private lateinit var rvMessages: RecyclerView
    private lateinit var etMessage: EditText
    private lateinit var btnSend: ImageButton
    private lateinit var adapter: ChatAdapter
    private lateinit var viewModel: ChatViewModel
    private lateinit var tvChatTitle: TextView

    private var otherUserId: Long = -1L
    private var conversationID: String? = null
    private var currentUserId: Long = -1L
    private var otherName: String? = null

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        arguments?.let {
            if (it.containsKey("otherUserID")) otherUserId = it.getLong("otherUserID", -1L)
            if (it.containsKey("conversationID")) conversationID = it.getString("conversationID")
            if (it.containsKey("otherName")) otherName = it.getString("otherName")
        }

        currentUserId = SharedPrefManager.getUserId(requireContext()) ?: -1L
    }

    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? {
        val v = inflater.inflate(R.layout.fragment_chat, container, false)
        tvChatTitle = v.findViewById(R.id.tvChatTitle)
        rvMessages = v.findViewById(R.id.rvMessages)
        etMessage = v.findViewById(R.id.etMessage)
        btnSend = v.findViewById(R.id.btnSend)

        otherName?.let { tvChatTitle.text = it; tvChatTitle.visibility = View.VISIBLE }

        adapter = ChatAdapter(currentUserId)
        rvMessages.setHasFixedSize(true)
        rvMessages.layoutManager = LinearLayoutManager(requireContext()).apply { stackFromEnd = true }
        rvMessages.adapter = adapter
        rvMessages.isNestedScrollingEnabled = true
        rvMessages.clipToPadding = false
        try { rvMessages.elevation = 8f * resources.displayMetrics.density } catch (_: Exception) {}

        // Adjust RecyclerView bottom padding so last message is visible above the input (post to ensure measurements are ready)
        try {
            v.post {
                try {
                    val input = v.findViewById<View?>(R.id.layoutInput)
                    val inputH = input?.height ?: (56 * resources.displayMetrics.density).toInt()
                    val extra = (8 * resources.displayMetrics.density).toInt()
                    rvMessages.setPadding(rvMessages.paddingLeft, rvMessages.paddingTop, rvMessages.paddingRight, inputH + extra)
                } catch (_: Exception) {}
            }
        } catch (_: Exception) {}

        val factory = ChatViewModelFactory(requireContext(), currentUserId)
        viewModel = ViewModelProvider(this, factory).get(ChatViewModel::class.java)

        viewModel.messages.observe(viewLifecycleOwner) { list ->
            val snapshot = list?.toList() ?: emptyList()
            val enriched = snapshot.map { msg ->
                if (msg.senderName != null) return@map msg
                val inferredName = when (msg.senderID) {
                    currentUserId -> SharedPrefManager.getUserFullName(requireContext()) ?: "You"
                    otherUserId -> otherName
                    else -> null
                }
                if (inferredName != null) msg.copy(senderName = inferredName) else msg
            }
            // Robust ordering: prefer sorting by parsed timestamps when there are multiple parseable times.
            fun parseTimeToMillis(t: String?): Long? {
                if (t == null) return null
                try { val asLong = t.toLongOrNull(); if (asLong != null) return if (asLong < 1000000000000L) asLong * 1000L else asLong } catch (_: Exception) {}
                try { return java.time.Instant.parse(t).toEpochMilli() } catch (_: Exception) {}
                try {
                    val fmt = java.time.format.DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss")
                    val ldt = java.time.LocalDateTime.parse(t, fmt)
                    return ldt.atZone(java.time.ZoneId.systemDefault()).toInstant().toEpochMilli()
                } catch (_: Exception) {}
                return null
            }

            val parsedList = enriched.map { it to try { parseTimeToMillis(it.timestamp) } catch (_: Exception) { null } }
            val parsedCount = parsedList.count { it.second != null }
            val sorted = when {
                parsedCount >= 2 -> {
                    // Sort by time ascending (oldest first)
                    parsedList.sortedBy { it.second ?: Long.MIN_VALUE }.map { it.first }
                }
                else -> {
                    // Not enough parseable times: apply heuristics on raw timestamp strings.
                    val rawTimestamps = enriched.mapNotNull { it.timestamp }
                    fun isLikelyIso(ts: String?): Boolean {
                        if (ts == null) return false
                        return ts.length >= 16 && ts[4] == '-' && ts[7] == '-' && ts[10] == ' '
                    }

                    // If first and last raw timestamps look like the common yyyy-MM-dd HH:mm:ss format, compare lexicographically.
                    val firstRaw = enriched.firstOrNull()?.timestamp
                    val lastRaw = enriched.lastOrNull()?.timestamp
                    val lexReverse = if (firstRaw != null && lastRaw != null && isLikelyIso(firstRaw) && isLikelyIso(lastRaw)) {
                        firstRaw > lastRaw
                    } else {
                        // Fallback heuristic: sample pairs across the list and count decreasing pairs (newest-first). If majority decreasing, reverse.
                        var dec = 0; var inc = 0
                        val sample = rawTimestamps.take(6)
                        for (i in 0 until sample.size - 1) {
                            val a = sample[i]; val b = sample[i+1]
                            if (a > b) dec++ else if (a < b) inc++
                        }
                        dec > inc && dec > 0
                    }

                    if (lexReverse) {
                        if (Log.isLoggable(TAG, Log.DEBUG)) Log.d(TAG, "Heuristic: reversing list because timestamps appear newest-first")
                        enriched.reversed()
                    } else enriched
                }
            }
            adapter.submitList(sorted)
            // Broadcast the newest message preview so the Conversations list can update its preview if needed
             try {
                 val lastMsg = sorted.lastOrNull()
                 if (lastMsg != null) {
                     // Determine partner id in case fragment was opened by conversationID (otherUserId may be -1)
                     val partnerIdToSave = try {
                         if (otherUserId != -1L) otherUserId
                         else {
                             val sid = lastMsg.senderID ?: -1L
                             val rid = lastMsg.receiverID ?: -1L
                             if (sid == currentUserId) rid else sid
                         }
                     } catch (_: Exception) { otherUserId }

                     // Persist preview for offline/slow-server situations
                     try {
                         com.example.capstone2.repository.SharedPrefManager.saveConversationPreview(requireContext(), partnerIdToSave, lastMsg.conversationID, lastMsg.message, lastMsg.timestamp)
                     } catch (_: Exception) {}

                     val itPreview = android.content.Intent("com.example.capstone2.NEW_MESSAGE_PREVIEW")
                     itPreview.putExtra("partnerID", partnerIdToSave)
                     itPreview.putExtra("conversationID", lastMsg.conversationID)
                     itPreview.putExtra("lastMessage", lastMsg.message)
                     itPreview.putExtra("lastMessageAt", lastMsg.timestamp)
                     requireActivity().sendBroadcast(itPreview)
                 }
             } catch (_: Exception) {}
             rvMessages.visibility = View.VISIBLE
            try { rvMessages.bringToFront() } catch (_: Exception) {}
            rvMessages.post { if (sorted.isNotEmpty()) try { rvMessages.scrollToPosition(sorted.size - 1) } catch (_: Exception) {} }
        }

        viewModel.error.observe(viewLifecycleOwner) { err -> err?.let { Toast.makeText(requireContext(), it, Toast.LENGTH_SHORT).show() } }

        if (conversationID != null) {
            Toast.makeText(requireContext(), "Loading conversation...", Toast.LENGTH_SHORT).show()
            viewModel.loadConversation(conversationID = conversationID)
        } else if (otherUserId != -1L) {
            Toast.makeText(requireContext(), "Loading conversation...", Toast.LENGTH_SHORT).show()
            viewModel.loadConversation(otherUserID = otherUserId)
        }

        btnSend.setOnClickListener {
            val text = etMessage.text.toString().trim()
            if (text.isEmpty()) return@setOnClickListener
            if (otherUserId == -1L) {
                Toast.makeText(requireContext(), "No recipient specified", Toast.LENGTH_SHORT).show()
                return@setOnClickListener
            }
            viewModel.sendMessage(receiverID = otherUserId, text = text, conversationID = conversationID)
            etMessage.setText("")
        }

        return v
    }

    companion object {
        private const val TAG = "ChatFragment"
        @JvmStatic
        fun newInstance(otherUserID: Long, conversationID: String? = null, otherName: String? = null) = ChatFragment().apply {
            arguments = Bundle().apply {
                putLong("otherUserID", otherUserID)
                conversationID?.let { putString("conversationID", it) }
                otherName?.let { putString("otherName", it) }
            }
        }
    }
}

package com.example.capstone2.messages

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.TextView
import androidx.fragment.app.Fragment
import androidx.lifecycle.lifecycleScope
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.capstone2.R
import com.example.capstone2.adapter.ConversationsAdapter
import com.example.capstone2.data.models.Conversation
import com.example.capstone2.network.ApiClient
import com.example.capstone2.repository.SharedPrefManager
import com.example.capstone2.customer.ChatFragment
import com.google.gson.Gson
import com.google.gson.JsonElement
import com.google.gson.JsonParser
import com.google.gson.reflect.TypeToken
import kotlinx.coroutines.launch
import android.util.Log

class MessagesFragment : Fragment() {

    private var rv: RecyclerView? = null
    private var tvEmpty: TextView? = null
    private lateinit var adapter: ConversationsAdapter

    companion object {
        private const val TAG = "MessagesFragment"
    }

    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? {
        val v = inflater.inflate(R.layout.fragment_messages_list, container, false)
        rv = v.findViewById(R.id.rvConversations)
        tvEmpty = v.findViewById(R.id.tvEmpty)

        adapter = ConversationsAdapter { conv ->
            // Open chat fragment for selected conversation using otherUserID (server will compute canonical conversationID)
            val chat = ChatFragment.newInstance(conv.partnerID, null, conv.partnerName)
            requireActivity().supportFragmentManager.beginTransaction()
                .replace(R.id.flFragment, chat)
                .addToBackStack(null)
                .commit()
        }

        rv?.layoutManager = LinearLayoutManager(requireContext())
        rv?.adapter = adapter

        loadConversations()

        return v
    }

    private fun loadConversations() {
        lifecycleScope.launch {
            try {
                val api = ApiClient.getApiService { SharedPrefManager.getAuthToken(requireContext()) }
                val resp = api.getConversations()
                if (resp.isSuccessful) {
                    val bodyStr = try { resp.body()?.string() } catch (_: Exception) { null }
                    if (Log.isLoggable(TAG, Log.DEBUG)) Log.d(TAG, "conversations raw: ${bodyStr?.take(2000)}")
                    if (bodyStr.isNullOrBlank()) {
                        this@MessagesFragment.showEmpty()
                        return@launch
                    }
                    try {
                        val gson = Gson()
                        val je: JsonElement = JsonParser.parseString(bodyStr)

                        // try find array under common keys
                        fun findArray(elem: JsonElement): JsonElement? {
                            if (elem.isJsonArray) return elem
                            if (elem.isJsonObject) {
                                val obj = elem.asJsonObject
                                val keys = listOf("conversations", "data", "items", "results")
                                for (k in keys) {
                                    if (obj.has(k)) {
                                        val child = obj.get(k)
                                        if (child.isJsonArray) return child
                                    }
                                }
                                // search properties for array
                                val it = obj.entrySet().iterator()
                                while (it.hasNext()) {
                                    val entry = it.next()
                                    val found = findArray(entry.value)
                                    if (found != null) return found
                                }
                            }
                            return null
                        }

                        val arr = findArray(je)
                        val listType = object : TypeToken<List<Conversation>>() {}.type
                        val conversations: List<Conversation> = if (arr != null) gson.fromJson(arr, listType) else gson.fromJson(bodyStr, listType)

                        // Enrich conversations with inferred partner names when missing
                        val enriched = mutableListOf<Conversation>()
                        // Determine a JsonArray to inspect (prefer `arr` if found)
                        val jsonArr = when {
                            arr != null && arr.isJsonArray -> arr.asJsonArray
                            je.isJsonArray -> je.asJsonArray
                            else -> null
                        }

                        if (jsonArr != null) {
                            for (i in 0 until jsonArr.size()) {
                                val elem = jsonArr[i]
                                try {
                                    val conv = gson.fromJson(elem, Conversation::class.java)
                                    if (conv != null) {
                                        var name = conv.partnerName
                                        if (name.isNullOrBlank() && elem.isJsonObject) {
                                            val obj = elem.asJsonObject
                                            // Common candidate keys for partner name (many variants)
                                            val keys = listOf(
                                                "partnerName", "partner_name", "partner", "partnerFullName",
                                                "partner_full_name", "name", "displayName", "display_name",
                                                "fullName", "full_name", "firstName", "first_name", "first_name"
                                            )
                                            for (k in keys) {
                                                if (obj.has(k) && !obj.get(k).isJsonNull) {
                                                    try { name = obj.get(k).asString; break } catch (_: Exception) {}
                                                }
                                            }
                                            // Check nested 'partner' or 'user' objects
                                            if (name.isNullOrBlank()) {
                                                val nestedKeys = listOf("partner", "user", "customer", "owner", "sender", "receiver", "profile", "participant")
                                                for (nk in nestedKeys) {
                                                    if (obj.has(nk) && obj.get(nk).isJsonObject) {
                                                        val p = obj.getAsJsonObject(nk)
                                                        val pk = listOf("fullName", "full_name", "name", "displayName", "display_name", "firstName", "first_name", "first_name")
                                                        for (k in pk) {
                                                            if (p.has(k) && !p.get(k).isJsonNull) {
                                                                try { name = p.get(k).asString; break } catch (_: Exception) {}
                                                            }
                                                        }
                                                        // Try first+last name snake or camel
                                                        if (name.isNullOrBlank()) {
                                                            val firstKeys = listOf("firstName", "first_name", "firstname")
                                                            val lastKeys = listOf("lastName", "last_name", "lastname")
                                                            var f: String? = null
                                                            var l: String? = null
                                                            for (fk in firstKeys) if (p.has(fk) && !p.get(fk).isJsonNull) try { f = p.get(fk).asString } catch (_: Exception) {}
                                                            for (lk in lastKeys) if (p.has(lk) && !p.get(lk).isJsonNull) try { l = p.get(lk).asString } catch (_: Exception) {}
                                                            if (!f.isNullOrBlank() || !l.isNullOrBlank()) {
                                                                name = listOfNotNull(f?.trim(), l?.trim()).joinToString(" ").trim()
                                                            }
                                                        }
                                                        if (!name.isNullOrBlank()) break
                                                    }
                                                }
                                            }
                                        }

                                        if (!name.isNullOrBlank() && name != conv.partnerName) {
                                            enriched.add(conv.copy(partnerName = name))
                                        } else {
                                            enriched.add(conv)
                                        }
                                    }
                                } catch (_: Exception) {
                                    // ignore parse error for this element
                                }
                            }
                        } else {
                            enriched.addAll(conversations)
                        }

                        if (enriched.isEmpty()) {
                            this@MessagesFragment.showEmpty()
                        } else {
                            tvEmpty?.visibility = View.GONE
                            // Submit initial list (with any inferred names)
                            adapter.submitList(enriched)

                            // Fetch missing partner names from API if any
                            val missingIds = enriched.filter { it.partnerName.isNullOrBlank() }.map { it.partnerID }.distinct()
                            if (missingIds.isNotEmpty()) {
                                // Simple in-memory cache for fetched user names
                                val userNameCache = mutableMapOf<Long, String>()

                                // Helper: try several endpoints to fetch a display name for a user id
                                suspend fun fetchDisplayNameForId(id: Long): String? {
                                    try {
                                        // First try typed endpoint if available
                                        try {
                                            val uresp = api.getUser(id)
                                            if (uresp.isSuccessful) {
                                                val user = uresp.body()
                                                if (user != null) {
                                                    val display = listOfNotNull(user.firstName.takeIf { it.isNotBlank() }, user.lastName.takeIf { it.isNotBlank() }).joinToString(" ").ifBlank { user.emailAddress }
                                                    if (display.isNotBlank()) return display
                                                }
                                            }
                                        } catch (_: Exception) {
                                            // typed endpoint may not exist or fail; continue to raw attempts
                                        }

                                        // Candidate raw endpoints relative to base URL â€” try them in order
                                        val candidates = listOf(
                                            "api/user/$id",
                                            "api/users/$id",
                                            "api/customers/$id",
                                            "api/customer/$id",
                                            "api/owners/$id",
                                            "api/owner/$id",
                                            "api/profile/$id",
                                            "api/users/$id/profile",
                                            "api/customers/$id/profile"
                                        )

                                        for (path in candidates) {
                                            try {
                                                if (Log.isLoggable(TAG, Log.DEBUG)) Log.d(TAG, "trying raw profile path: $path for id=$id")
                                                val r = api.getRaw(path)
                                                if (!r.isSuccessful) {
                                                    if (Log.isLoggable(TAG, Log.DEBUG)) Log.d(TAG, "raw path $path returned HTTP ${r.code()}")
                                                    continue
                                                }
                                                val raw = try { r.body()?.string() } catch (_: Exception) { null }
                                                if (raw.isNullOrBlank()) continue
                                                if (Log.isLoggable(TAG, Log.DEBUG)) Log.d(TAG, "raw profile response for $path: ${raw.take(2000)}")

                                                try {
                                                    val je2 = JsonParser.parseString(raw)
                                                    fun extractName(elem: JsonElement?): String? {
                                                        if (elem == null) return null
                                                        if (elem.isJsonObject) {
                                                            val obj2 = elem.asJsonObject
                                                            val keys2 = listOf("name","displayName","display_name","fullName","full_name","firstName","first_name","firstname")
                                                            for (k2 in keys2) {
                                                                if (obj2.has(k2) && !obj2.get(k2).isJsonNull) {
                                                                    try { val s = obj2.get(k2).asString; if (!s.isNullOrBlank()) return s } catch (_: Exception) {}
                                                                }
                                                            }
                                                            val firstKeys2 = listOf("firstName","first_name","firstname")
                                                            val lastKeys2 = listOf("lastName","last_name","lastname")
                                                            var f2: String? = null; var l2: String? = null
                                                            for (fk2 in firstKeys2) if (obj2.has(fk2) && !obj2.get(fk2).isJsonNull) try { f2 = obj2.get(fk2).asString } catch (_: Exception) {}
                                                            for (lk2 in lastKeys2) if (obj2.has(lk2) && !obj2.get(lk2).isJsonNull) try { l2 = obj2.get(lk2).asString } catch (_: Exception) {}
                                                            if (!f2.isNullOrBlank() || !l2.isNullOrBlank()) return listOfNotNull(f2?.trim(), l2?.trim()).joinToString(" ").trim()
                                                            val nested2 = listOf("user","partner","customer","owner","profile","data")
                                                            for (nk2 in nested2) {
                                                                if (obj2.has(nk2) && obj2.get(nk2).isJsonObject) {
                                                                    val maybe = extractName(obj2.get(nk2))
                                                                    if (!maybe.isNullOrBlank()) return maybe
                                                                }
                                                            }
                                                        }
                                                        if (elem.isJsonArray) {
                                                            val arr2 = elem.asJsonArray
                                                            if (arr2.size() > 0) {
                                                                val first = arr2[0]
                                                                return extractName(first)
                                                            }
                                                        }
                                                        return null
                                                    }

                                                    val found = extractName(je2)
                                                    if (!found.isNullOrBlank()) return found
                                                } catch (_: Exception) {
                                                    // parsing failed, try next
                                                }
                                            } catch (_: Exception) {
                                                // raw GET failed, try next
                                            }
                                        }
                                    } catch (_: Exception) {
                                        // overall failure, return null
                                    }
                                    return null
                                }

                                for (id in missingIds) {
                                    try {
                                        if (Log.isLoggable(TAG, Log.DEBUG)) Log.d(TAG, "fetching partner profile for userId=$id")
                                        val display = fetchDisplayNameForId(id)
                                        if (!display.isNullOrBlank()) {
                                            if (Log.isLoggable(TAG, Log.DEBUG)) Log.d(TAG, "fetched user $id display='$display'")
                                            userNameCache[id] = display
                                        } else {
                                            if (Log.isLoggable(TAG, Log.DEBUG)) Log.d(TAG, "no profile name found for id=$id")
                                        }
                                    } catch (_: Exception) {
                                        Log.w(TAG, "getUser($id) failed")
                                        // ignore individual fetch errors
                                    }
                                }

                                if (userNameCache.isNotEmpty()) {
                                    val updated = enriched.map { c ->
                                        if (c.partnerName.isNullOrBlank() && userNameCache.containsKey(c.partnerID)) c.copy(partnerName = userNameCache[c.partnerID]) else c
                                    }
                                    adapter.submitList(updated)
                                }
                            }
                        }

                    } catch (e: Exception) {
                        Log.e(TAG, "parse convs", e)
                        this@MessagesFragment.showEmpty()
                    }
                } else {
                    this@MessagesFragment.showEmpty()
                }
            } catch (e: Exception) {
                Log.e(TAG, "loadConversations", e)
                this@MessagesFragment.showEmpty()
            }
        }
    }

    private fun showEmpty() {
        adapter.submitList(emptyList())
        tvEmpty?.visibility = View.VISIBLE
    }
}

package com.example.capstone2.messages

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.TextView
import android.widget.Toast
import androidx.appcompat.app.AlertDialog
import androidx.fragment.app.Fragment
import androidx.lifecycle.lifecycleScope
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.capstone2.R
import com.example.capstone2.adapter.ConversationsAdapter
import com.example.capstone2.data.models.Conversation
import com.example.capstone2.network.ApiClient
import com.example.capstone2.repository.SharedPrefManager
import com.example.capstone2.customer.ChatFragment
import com.google.gson.Gson
import com.google.gson.JsonElement
import com.google.gson.JsonParser
import com.google.gson.reflect.TypeToken
import kotlinx.coroutines.launch
import android.util.Log
import java.io.File
import android.content.ClipData
import android.content.ClipboardManager
import android.content.Context
import android.content.Intent
import android.widget.ScrollView
import android.content.pm.ApplicationInfo
import android.content.BroadcastReceiver
import android.content.IntentFilter

class MessagesFragment : Fragment() {

    private var rv: RecyclerView? = null
    private var tvEmpty: TextView? = null
    private lateinit var adapter: ConversationsAdapter
    private var previewReceiver: BroadcastReceiver? = null

    companion object {
        private const val TAG = "MessagesFragment"
    }

    // Runtime check for whether the app is debuggable (avoids referencing BuildConfig)
    private fun isDebug(): Boolean {
        return try {
            (requireContext().applicationInfo.flags and ApplicationInfo.FLAG_DEBUGGABLE) != 0
        } catch (_: Exception) { false }
    }

    // Reusable timestamp parser for this fragment
    private fun parseTimeToMillis(t: String?): Long? {
        if (t == null) return null
        try { val asLong = t.toLongOrNull(); if (asLong != null) return if (asLong < 1000000000000L) asLong * 1000L else asLong } catch (_: Exception) {}
        try { return java.time.Instant.parse(t).toEpochMilli() } catch (_: Exception) {}
        try {
            val fmt = java.time.format.DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss")
            val ldt = java.time.LocalDateTime.parse(t, fmt)
            return ldt.atZone(java.time.ZoneId.systemDefault()).toInstant().toEpochMilli()
        } catch (_: Exception) {}
        return null
    }

    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? {
        val v = inflater.inflate(R.layout.fragment_messages_list, container, false)
        rv = v.findViewById(R.id.rvConversations)
        tvEmpty = v.findViewById(R.id.tvEmpty)

        adapter = ConversationsAdapter { conv ->
            // Open chat fragment for selected conversation using otherUserID (server will compute canonical conversationID)
            val chat = ChatFragment.newInstance(conv.partnerID, null, conv.partnerName)
            requireActivity().supportFragmentManager.beginTransaction()
                .replace(R.id.flFragment, chat)
                .addToBackStack(null)
                .commit()
        }

        rv?.layoutManager = LinearLayoutManager(requireContext())
        rv?.adapter = adapter

        // Register receiver to update conversation preview when chat receives/sends a new message
        try {
            previewReceiver = object : BroadcastReceiver() {
                override fun onReceive(context: Context?, intent: Intent?) {
                    try {
                        if (intent == null) return
                        val partnerId = intent.getLongExtra("partnerID", -1L)
                        if (partnerId == -1L) return
                        val convId = intent.getStringExtra("conversationID")
                        val lastMsg = intent.getStringExtra("lastMessage")
                        var lastAt = intent.getStringExtra("lastMessageAt")
                        if (lastAt.isNullOrBlank()) {
                            try {
                                val fmt = java.time.format.DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss")
                                lastAt = java.time.LocalDateTime.now().format(fmt)
                            } catch (_: Exception) { lastAt = null }
                        }
                        // Update adapter list: replace or insert, then sort newest-first
                        val current = adapter.currentList.toMutableList()
                        var replaced = false
                        for (i in current.indices) {
                            if (current[i].partnerID == partnerId) {
                                val c = current[i]
                                val updated = c.copy(conversationID = convId ?: c.conversationID, lastMessage = lastMsg ?: c.lastMessage, lastMessageAt = lastAt ?: c.lastMessageAt)
                                current[i] = updated
                                replaced = true
                                break
                            }
                        }
                        if (!replaced) {
                            // create a minimal conversation entry when missing
                            val newConv = Conversation(conversationID = convId ?: "", partnerID = partnerId, partnerName = null, lastMessage = lastMsg ?: "", lastMessageAt = lastAt, unreadCount = 0)
                            current.add(newConv)
                        }
                        // Sort newest-first by parsed timestamp
                        try {
                            val sorted = current.sortedWith(compareByDescending<Conversation> { c -> parseTimeToMillis(c.lastMessageAt) ?: Long.MIN_VALUE })
                            adapter.submitList(sorted)
                        } catch (_: Exception) {
                            adapter.submitList(current)
                        }
                    } catch (_: Exception) { }
                }
            }
            requireContext().registerReceiver(previewReceiver, IntentFilter("com.example.capstone2.NEW_MESSAGE_PREVIEW"))
        } catch (_: Exception) { }

        loadConversations()

        return v
    }

    private fun loadConversations() {
        lifecycleScope.launch {
            try {
                val api = ApiClient.getApiService { SharedPrefManager.getAuthToken(requireContext()) }
                val resp = api.getConversations()
                if (resp.isSuccessful) {
                    val bodyStr = try { resp.body()?.string() } catch (_: Exception) { null }

                    // Save raw response to external app-specific storage so you can pull it without adb
                    try {
                        val outDir = requireContext().getExternalFilesDir(null)
                        if (outDir != null && !bodyStr.isNullOrBlank()) {
                            val outFile = File(outDir, "conversations_raw_${System.currentTimeMillis()}.json")
                            outFile.writeText(bodyStr)
                            Toast.makeText(requireContext(), "Saved conversations JSON to: ${outFile.absolutePath}", Toast.LENGTH_LONG).show()
                            // debug logging removed
 // ...existing code...
                    // debug logging removed
                    if (bodyStr.isNullOrBlank()) {
                        this@MessagesFragment.showEmpty()
                        return@launch
                    }
                    try {
                        val gson = Gson()
                        val je: JsonElement = JsonParser.parseString(bodyStr)

                        // try find array under common keys
                        fun findArray(elem: JsonElement): JsonElement? {
                            if (elem.isJsonArray) return elem
                            if (elem.isJsonObject) {
                                val obj = elem.asJsonObject
                                val keys = listOf("conversations", "data", "items", "results")
                                for (k in keys) {
                                    if (obj.has(k)) {
                                        val child = obj.get(k)
                                        if (child.isJsonArray) return child
                                    }
                                }
                                // search properties for array
                                val it = obj.entrySet().iterator()
                                while (it.hasNext()) {
                                    val entry = it.next()
                                    val found = findArray(entry.value)
                                    if (found != null) return found
                                }
                            }
                            return null
                        }

                        val arr = findArray(je)
                        val listType = object : TypeToken<List<Conversation>>() {}.type
                        val conversations: List<Conversation> = if (arr != null) gson.fromJson(arr, listType) else gson.fromJson(bodyStr, listType)

                        // Enrich conversations with inferred partner names when missing
                        val enriched = mutableListOf<Conversation>()
                        // Determine a JsonArray to inspect (prefer `arr` if found)
                        val jsonArr = when {
                            arr != null && arr.isJsonArray -> arr.asJsonArray
                            je.isJsonArray -> je.asJsonArray
                            else -> null
                        }

                        // Helper: extract latest message text and timestamp from a JsonElement conversation candidate
                        fun extractLatestMessage(elem: JsonElement?): Pair<String?, String?> {
                            if (elem == null) return Pair(null, null)
                            try {
                                if (!elem.isJsonObject) return Pair(null, null)
                                val obj = elem.asJsonObject

                                // Quick string fields that often contain preview
                                val quickKeys = listOf("lastMessage", "last_message", "latestMessage", "latest_message", "preview", "last")
                                val timeKeysSibling = listOf("lastMessageAt", "last_message_at", "lastAt", "last_at", "updatedAt", "updated_at", "lastMessageTime")
                                for (k in quickKeys) {
                                    if (obj.has(k) && !obj.get(k).isJsonNull) {
                                        try {
                                            val s = obj.get(k).asString
                                            if (!s.isNullOrBlank()) {
                                                // Try to find a sibling timestamp in the same object
                                                var siblingTime: String? = null
                                                for (tk in timeKeysSibling) {
                                                    if (obj.has(tk) && !obj.get(tk).isJsonNull) {
                                                        try { siblingTime = obj.get(tk).asString; break } catch (_: Exception) { }
                                                    }
                                                }
                                                return Pair(s, siblingTime)
                                            }
                                        } catch (_: Exception) { }
                                    }
                                }

                                // If there's an object representing the latest message, try to extract text + time
                                val objKeys = listOf("lastMessageObject", "latest", "latest_message", "last_message_object", "last_message_obj")
                                for (k in objKeys) {
                                    if (obj.has(k) && obj.get(k).isJsonObject) {
                                        val m = obj.getAsJsonObject(k)
                                        val textKeys = listOf("body", "message", "text", "content")
                                        var txt: String? = null
                                        for (tk in textKeys) if (m.has(tk) && !m.get(tk).isJsonNull) try { txt = m.get(tk).asString; break } catch (_: Exception) { }
                                        val timeKeys = listOf("createdAt","created_at","sentAt","sent_at","time","timestamp","date")
                                        var t: String? = null
                                        for (tk in timeKeys) if (m.has(tk) && !m.get(tk).isJsonNull) try { t = m.get(tk).asString; break } catch (_: Exception) { }
                                        if (!txt.isNullOrBlank()) return Pair(txt, t)
                                    }
                                }

                                // If there is an array of messages, try to pick the most recent by timestamp when possible,
                                // otherwise determine array ordering using first/last timestamps or fall back to last element.
                                val arrKeys = listOf("messages", "items", "results", "conversationMessages", "msgs")
                                for (k in arrKeys) {
                                    if (obj.has(k) && obj.get(k).isJsonArray) {
                                        val a = obj.getAsJsonArray(k)
                                        if (a.size() == 0) continue

                                        // Build a list of entries with text + parsed time (if present)
                                        data class Entry(val text: String?, val timeStr: String?, val timeMillis: Long?)
                                        val entries = mutableListOf<Entry>()
                                        for (j in 0 until a.size()) {
                                            val jelem = a[j]
                                            if (!jelem.isJsonObject) { entries.add(Entry(null, null, null)); continue }
                                            val mobj = jelem.asJsonObject
                                            var txt: String? = null
                                            for (tk in listOf("body","message","text","content","lastMessage")) if (mobj.has(tk) && !mobj.get(tk).isJsonNull) try { txt = mobj.get(tk).asString; break } catch (_: Exception) { }
                                            var timestr: String? = null
                                            for (tk in listOf("createdAt","created_at","sentAt","sent_at","time","timestamp","date")) if (mobj.has(tk) && !mobj.get(tk).isJsonNull) try { timestr = mobj.get(tk).asString; break } catch (_: Exception) { }
                                            var timeVal: Long? = null
                                            if (!timestr.isNullOrBlank()) {
                                                try { timeVal = timestr.toLong() } catch (_: Exception) {
                                                    try { timeVal = java.time.Instant.parse(timestr).toEpochMilli() } catch (_: Exception) { timeVal = null }
                                                }
                                            }
                                            entries.add(Entry(txt, timestr, timeVal))
                                        }

                                        // If any time values are parseable, pick the entry with max timestamp
                                        val anyTimes = entries.any { it.timeMillis != null }
                                        if (anyTimes) {
                                            val best = entries.maxByOrNull { it.timeMillis ?: Long.MIN_VALUE }
                                            if (best != null && !best.text.isNullOrBlank()) return Pair(best.text, best.timeStr)
                                        }

                                        // If no times parseable, try to detect ordering by comparing first/last timestamps when present
                                        if (entries.size >= 2) {
                                            val first = entries.first()
                                            val last = entries.last()
                                            val ftime = first.timeMillis
                                            val ltime = last.timeMillis
                                            if (ftime != null && ltime != null) {
                                                // if first >= last, array is newest-first; else newest is last
                                                return if (ftime >= ltime) Pair(first.text, first.timeStr) else Pair(last.text, last.timeStr)
                                            } else if (ftime != null) {
                                                return Pair(first.text, first.timeStr)
                                            } else if (ltime != null) {
                                                return Pair(last.text, last.timeStr)
                                            }
                                        }

                                        // No parseable timestamps found. Prefer the first element as a fallback because many
                                        // APIs return messages in newest-first order. If first is empty, fall back to last.
                                        val firstFallback = entries.firstOrNull()?.text
                                        if (!firstFallback.isNullOrBlank()) {
                                            // debug logging removed
                                            return Pair(firstFallback, null)
                                        }
                                        val lastFallback = entries.lastOrNull()?.text
                                        if (!lastFallback.isNullOrBlank()) {
                                            // debug logging removed
                                            return Pair(lastFallback, null)
                                        }
                                     }
                                }

                                // Nothing found
                            } catch (_: Exception) { }
                            return Pair(null, null)
                        }

                        // Helper: parse timestamp string to epoch millis (tries plain long, ISO instant)
                        fun parseTimeToMillis(t: String?): Long? {
                            if (t == null) return null
                            try {
                                // Try numeric epoch millis or seconds
                                val asLong = t.toLongOrNull()
                                if (asLong != null) {
                                    // Heuristic: if number looks like seconds (10 digits), convert to millis
                                    return if (asLong < 1000000000000L) asLong * 1000L else asLong
                                }
                            } catch (_: Exception) { }
                            try {
                                return java.time.Instant.parse(t).toEpochMilli()
                            } catch (_: Exception) { }
                            // Try common custom formats like "yyyy-MM-dd HH:mm:ss"
                            try {
                                val fmt = java.time.format.DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss")
                                val ldt = java.time.LocalDateTime.parse(t, fmt)
                                return ldt.atZone(java.time.ZoneId.systemDefault()).toInstant().toEpochMilli()
                            } catch (_: Exception) { }
                            // Try a variant with timezone offset if present
                            try {
                                val fmt2 = java.time.format.DateTimeFormatter.ofPattern("yyyy-MM-dd'T'HH:mm:ss.SSSXXX")
                                val zdt = java.time.ZonedDateTime.parse(t, fmt2)
                                return zdt.toInstant().toEpochMilli()
                            } catch (_: Exception) { }
                            return null
                        }

                        if (jsonArr != null) {
                            for (i in 0 until jsonArr.size()) {
                                val elem = jsonArr[i]
                                try {
                                    val conv = gson.fromJson(elem, Conversation::class.java)
                                    if (conv != null) {
                                        var name = conv.partnerName

                                        // existing name inference logic preserved
                                        if (name.isNullOrBlank() && elem.isJsonObject) {
                                            val obj = elem.asJsonObject
                                            // Common candidate keys for partner name (many variants)
                                            val keys = listOf(
                                                "partnerName", "partner_name", "partner", "partnerFullName",
                                                "partner_full_name", "name", "displayName", "display_name",
                                                "fullName", "full_name", "firstName", "first_name", "first_name"
                                            )
                                            for (k in keys) {
                                                if (obj.has(k) && !obj.get(k).isJsonNull) {
                                                    try { name = obj.get(k).asString; break } catch (_: Exception) {}
                                                }
                                            }
                                            // Check nested 'partner' or 'user' objects
                                            if (name.isNullOrBlank()) {
                                                val nestedKeys = listOf("partner", "user", "customer", "owner", "sender", "receiver", "profile", "participant")
                                                for (nk in nestedKeys) {
                                                    if (obj.has(nk) && obj.get(nk).isJsonObject) {
                                                        val p = obj.getAsJsonObject(nk)
                                                        val pk = listOf("fullName", "full_name", "name", "displayName", "display_name", "firstName", "first_name", "first_name")
                                                        for (k in pk) {
                                                            if (p.has(k) && !p.get(k).isJsonNull) {
                                                                try { name = p.get(k).asString; break } catch (_: Exception) {}
                                                            }
                                                        }
                                                        // Try first+last name snake or camel
                                                        if (name.isNullOrBlank()) {
                                                            val firstKeys = listOf("firstName", "first_name", "firstname")
                                                            val lastKeys = listOf("lastName", "last_name", "lastname")
                                                            var f: String? = null
                                                            var l: String? = null
                                                            for (fk in firstKeys) if (p.has(fk) && !p.get(fk).isJsonNull) try { f = p.get(fk).asString } catch (_: Exception) {}
                                                            for (lk in lastKeys) if (p.has(lk) && !p.get(lk).isJsonNull) try { l = p.get(lk).asString } catch (_: Exception) {}
                                                            if (!f.isNullOrBlank() || !l.isNullOrBlank()) {
                                                                name = listOfNotNull(f?.trim(), l?.trim()).joinToString(" ").trim()
                                                            }
                                                        }
                                                        if (!name.isNullOrBlank()) break
                                                    }
                                                }
                                            }
                                        }

                                        // --- NEW: extract latest message preview and timestamp when available ---
                                        val (previewText, previewAt) = extractLatestMessage(elem)

                                        // Decide whether to override conv.lastMessage with the extracted preview.
                                        // Only override when:
                                        //  - conv.lastMessage is blank/null, OR
                                        //  - previewAt is parseable and newer than conv.lastMessageAt.
                                        var finalConv = conv
                                        try {
                                            val convTime = parseTimeToMillis(conv.lastMessageAt)
                                            val previewTime = parseTimeToMillis(previewAt)
                                            if (conv.lastMessage.isNullOrBlank()) {
                                                if (!previewText.isNullOrBlank()) finalConv = conv.copy(lastMessage = previewText, lastMessageAt = previewAt ?: conv.lastMessageAt)
                                            } else if (!previewText.isNullOrBlank() && previewTime != null) {
                                                // if preview timestamp is newer than conv timestamp, prefer preview
                                                if (convTime == null || previewTime > convTime) {
                                                    finalConv = conv.copy(lastMessage = previewText, lastMessageAt = previewAt ?: conv.lastMessageAt)
                                                }
                                            }
                                        } catch (_: Exception) {
                                            // fallback: do not override
                                            finalConv = conv
                                        }

                                        if (!name.isNullOrBlank() && name != finalConv.partnerName) {
                                            enriched.add(finalConv.copy(partnerName = name))
                                        } else {
                                            enriched.add(finalConv)
                                        }
                                    }
                                } catch (_: Exception) {
                                    // ignore parse error for this element
                                }
                            }
                        } else {
                            enriched.addAll(conversations)
                        }

                        if (enriched.isEmpty()) {
                            this@MessagesFragment.showEmpty()
                        } else {
                            tvEmpty?.visibility = View.GONE
                            // NOTE: we used to submit `enriched` here immediately, but that produced
                            // duplicate rows and sometimes showed older previews. The final
                            // collapsed/deduped/sorted list is submitted later in this function.
                            // So defer submitting until we've collapsed/sorted/deduped below.

                            // Fetching partner names is handled after we compute `deduped` and submit the final list below.
                        }

                        // Compute final deduped/sorted list for display
                        val deduped = enriched
                            .groupBy { it.partnerID }
                            .map { it.value.maxByOrNull { c -> parseTimeToMillis(c.lastMessageAt) ?: 0L } }
                            .filterNotNull()
                            .sortedWith(compareByDescending<Conversation> { c -> parseTimeToMillis(c.lastMessageAt) ?: 0L })

                        // Merge cached previews (saved by ChatFragment) so UI reflects recent local messages
                        val finalList: List<Conversation> = try {
                            val merged = deduped.map { conv ->
                                try {
                                    val preview = com.example.capstone2.repository.SharedPrefManager.getConversationPreview(requireContext(), conv.partnerID)
                                    if (preview != null) {
                                        val serverTime = parseTimeToMillis(conv.lastMessageAt)
                                        val previewTime = parseTimeToMillis(preview.lastMessageAt)
                                        if (!preview.lastMessage.isNullOrBlank() && (previewTime != null && (serverTime == null || previewTime > serverTime))) {
                                            // preview is newer; use it
                                            return@map conv.copy(lastMessage = preview.lastMessage, lastMessageAt = preview.lastMessageAt)
                                        }
                                    }
                                } catch (_: Exception) { }
                                conv
                            }
                            // Re-sort after merging
                            merged.sortedWith(compareByDescending<Conversation> { c -> parseTimeToMillis(c.lastMessageAt) ?: 0L })
                        } catch (_: Exception) {
                            deduped
                        }
                        adapter.submitList(finalList)

                        // Debug: prepare and log the final list submitted to the adapter
                        try {
                            val sb = StringBuilder()
                            sb.append("final-list: ")
                            for (c in finalList) {
                                sb.append("[id=${c.conversationID},partner=${c.partnerID},name=${c.partnerName},time=${c.lastMessageAt}] ")
                            }
                            // debug logging removed
                        } catch (_: Exception) { }

                        // Fetch missing partner names from API if any
                        val missingIds = deduped.filter { it.partnerName.isNullOrBlank() }.map { it.partnerID }.distinct()
                         if (missingIds.isNotEmpty()) {
                            // Simple in-memory cache for fetched user names
                            val userNameCache = mutableMapOf<Long, String>()

                            // Helper: try several endpoints to fetch a display name for a user id
                            suspend fun fetchDisplayNameForId(id: Long): String? {
                                try {
                                    // First try typed endpoint if available
                                    try {
                                        val uresp = api.getUser(id)
                                        if (uresp.isSuccessful) {
                                            val user = uresp.body()
                                            if (user != null) {
                                                val display = listOfNotNull(user.firstName.takeIf { it.isNotBlank() }, user.lastName.takeIf { it.isNotBlank() }).joinToString(" ").ifBlank { user.emailAddress }
                                                if (display.isNotBlank()) return display
                                            }
                                        }
                                    } catch (_: Exception) {
                                        // typed endpoint may not exist or fail; continue to raw attempts
                                    }

                                    // Candidate raw endpoints relative to base URL â€” try them in order
                                    val candidates = listOf(
                                        "api/user/$id",
                                        "api/users/$id",
                                        "api/customers/$id",
                                        "api/customer/$id",
                                        "api/owners/$id",
                                        "api/owner/$id",
                                        "api/profile/$id",
                                        "api/users/$id/profile",
                                        "api/customers/$id/profile"
                                    )

                                    for (path in candidates) {
                                        try {
                                            // debug logging removed
                                            val r = api.getRaw(path)
                                            if (!r.isSuccessful) {
                                                // debug logging removed
                                                continue
                                            }
                                            val raw = try { r.body()?.string() } catch (_: Exception) { null }
                                            if (raw.isNullOrBlank()) continue
                                            // debug logging removed

                                            try {
                                                val je2 = JsonParser.parseString(raw)
                                                fun extractName(elem: JsonElement?): String? {
                                                    if (elem == null) return null
                                                    if (elem.isJsonObject) {
                                                        val obj2 = elem.asJsonObject
                                                        val keys2 = listOf("name","displayName","display_name","fullName","full_name","firstName","first_name","firstname")
                                                        for (k2 in keys2) {
                                                            if (obj2.has(k2) && !obj2.get(k2).isJsonNull) {
                                                                try { val s = obj2.get(k2).asString; if (!s.isNullOrBlank()) return s } catch (_: Exception) {}
                                                            }
                                                        }
                                                        val firstKeys2 = listOf("firstName","first_name","firstname")
                                                        val lastKeys2 = listOf("lastName","last_name","lastname")
                                                        var f2: String? = null; var l2: String? = null
                                                        for (fk2 in firstKeys2) if (obj2.has(fk2) && !obj2.get(fk2).isJsonNull) try { f2 = obj2.get(fk2).asString } catch (_: Exception) {}
                                                        for (lk2 in lastKeys2) if (obj2.has(lk2) && !obj2.get(lk2).isJsonNull) try { l2 = obj2.get(lk2).asString } catch (_: Exception) {}
                                                        if (!f2.isNullOrBlank() || !l2.isNullOrBlank()) return listOfNotNull(f2?.trim(), l2?.trim()).joinToString(" ").trim()
                                                        val nested2 = listOf("user","partner","customer","owner","profile","data")
                                                        for (nk2 in nested2) {
                                                            if (obj2.has(nk2) && obj2.get(nk2).isJsonObject) {
                                                                val maybe = extractName(obj2.get(nk2))
                                                                if (!maybe.isNullOrBlank()) return maybe
                                                            }
                                                        }
                                                    }
                                                    if (elem.isJsonArray) {
                                                        val arr2 = elem.asJsonArray
                                                        if (arr2.size() > 0) {
                                                            val first = arr2[0]
                                                            return extractName(first)
                                                        }
                                                    }
                                                    return null
                                                }

                                                val found = extractName(je2)
                                                if (!found.isNullOrBlank()) return found
                                            } catch (_: Exception) {
                                                // parsing failed, try next
                                            }
                                        } catch (_: Exception) {
                                            // raw GET failed, try next
                                        }
                                    }
                                } catch (_: Exception) {
                                    // overall failure, return null
                                }
                                return null
                            }

                            for (id in missingIds) {
                                try {
                                    // debug logging removed
                                    val display = fetchDisplayNameForId(id)
                                    if (!display.isNullOrBlank()) {
                                        // debug logging removed
                                        userNameCache[id] = display
                                    } else {
                                        // debug logging removed
                                    }
                                } catch (_: Exception) {
                                    // ignore individual fetch errors
                                }
                            }

                            if (userNameCache.isNotEmpty()) {
                                val updated = deduped.map { c ->
                                    if (c.partnerName.isNullOrBlank() && userNameCache.containsKey(c.partnerID)) c.copy(partnerName = userNameCache[c.partnerID]) else c
                                }
                                val updatedSorted = updated.sortedWith(compareByDescending<Conversation> { c -> parseTimeToMillis(c.lastMessageAt) ?: 0L })
                                adapter.submitList(updatedSorted)
                            }
                        }

                    } catch (e: Exception) {
                        Log.e(TAG, "parse convs", e)
                        this@MessagesFragment.showEmpty()
                    }
                } else {
                    this@MessagesFragment.showEmpty()
                }
            } catch (e: Exception) {
                Log.e(TAG, "loadConversations", e)
                this@MessagesFragment.showEmpty()
            }
        }
    }

    private fun showEmpty() {
        adapter.submitList(emptyList())
        tvEmpty?.visibility = View.VISIBLE
    }

    override fun onDestroyView() {
        try { previewReceiver?.let { requireContext().unregisterReceiver(it) } } catch (_: Exception) {}
        previewReceiver = null
        super.onDestroyView()
    }
}

package com.example.capstone2.repository

import android.content.Context

object SharedPrefManager {
    private const val PREFS_NAME = "capstone_prefs"

    /**
     * Safely returns the stored userID from known preference files if present.
     * Accepts values stored as Long, Int, String (numeric), Float, Double.
     */
    fun getUserId(ctx: Context): Long? {
        val names = arrayOf("capstone_prefs", "MyAppPrefs")
        for (n in names) {
            val p = ctx.getSharedPreferences(n, Context.MODE_PRIVATE)
            if (!p.contains("userID")) continue
            val v = p.all["userID"]
            when (v) {
                is Long -> return v
                is Int -> return v.toLong()
                is String -> return v.toLongOrNull()
                is Float -> return v.toLong()
                is Double -> return v.toLong()
                else -> continue
            }
        }
        return null
    }

    fun saveAuthToken(ctx: Context, token: String) {
        val p = ctx.getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
        p.edit().putString("auth_token", token).apply()
    }

    /**
     * Try to find the auth token in any of the known preference files.
     * This is defensive because some activities use "MyAppPrefs" while the
     * centralized SharedPrefManager historically used "capstone_prefs".
     */
    fun getAuthToken(ctx: Context): String? {
        val names = arrayOf(PREFS_NAME, "MyAppPrefs")
        for (n in names) {
            val p = ctx.getSharedPreferences(n, Context.MODE_PRIVATE)
            val t = p.getString("auth_token", null)
            if (!t.isNullOrBlank()) return t
        }
        return null
    }

    fun saveUserId(ctx: Context, userId: Long) {
        val p = ctx.getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
        p.edit().putLong("userID", userId).apply()
    }

    /**
     * Try to return a user-friendly name for the currently-signed-in user from shared prefs.
     * Checks several common keys (`userFullName`, `displayName`, `firstName`/`lastName`) and
     * returns the first non-empty value found.
     */
    fun getUserFullName(ctx: Context): String? {
        val p = ctx.getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
        val candidates = listOf(
            p.getString("userFullName", null),
            p.getString("displayName", null),
            p.getString("userName", null)
        ).filterNotNull().map { it.trim() }.filter { it.isNotEmpty() }
        if (candidates.isNotEmpty()) return candidates.first()

        // Try to assemble from first/last name if available
        val first = p.getString("firstName", null)
        val last = p.getString("lastName", null)
        if (!first.isNullOrBlank() || !last.isNullOrBlank()) {
            return listOfNotNull(first?.trim(), last?.trim()).joinToString(" ")
        }

        return null
    }

    // --- debug panel preference helpers ---
    private const val KEY_DEBUG_PANEL_VISIBLE = "debug_panel_visible"

    fun isDebugPanelEnabled(ctx: Context): Boolean {
        val p = ctx.getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
        return p.getBoolean(KEY_DEBUG_PANEL_VISIBLE, false)
    }

    fun setDebugPanelEnabled(ctx: Context, enabled: Boolean) {
        val p = ctx.getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
        p.edit().putBoolean(KEY_DEBUG_PANEL_VISIBLE, enabled).apply()
    }
}

package com.example.capstone2.repository

import android.content.Context

object SharedPrefManager {
    private const val PREFS_NAME = "capstone_prefs"

    /**
     * Safely returns the stored userID from known preference files if present.
     * Accepts values stored as Long, Int, String (numeric), Float, Double.
     */
    fun getUserId(ctx: Context): Long? {
        val names = arrayOf("capstone_prefs", "MyAppPrefs")
        for (n in names) {
            val p = ctx.getSharedPreferences(n, Context.MODE_PRIVATE)
            if (!p.contains("userID")) continue
            val v = p.all["userID"]
            when (v) {
                is Long -> return v
                is Int -> return v.toLong()
                is String -> return v.toLongOrNull()
                is Float -> return v.toLong()
                is Double -> return v.toLong()
                else -> continue
            }
        }
        return null
    }

    fun saveAuthToken(ctx: Context, token: String) {
        val p = ctx.getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
        p.edit().putString("auth_token", token).apply()
    }

    /**
     * Try to find the auth token in any of the known preference files.
     * This is defensive because some activities use "MyAppPrefs" while the
     * centralized SharedPrefManager historically used "capstone_prefs".
     */
    fun getAuthToken(ctx: Context): String? {
        val names = arrayOf(PREFS_NAME, "MyAppPrefs")
        for (n in names) {
            val p = ctx.getSharedPreferences(n, Context.MODE_PRIVATE)
            val t = p.getString("auth_token", null)
            if (!t.isNullOrBlank()) return t
        }
        return null
    }

    fun saveUserId(ctx: Context, userId: Long) {
        val p = ctx.getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
        p.edit().putLong("userID", userId).apply()
    }

    /**
     * Try to return a user-friendly name for the currently-signed-in user from shared prefs.
     * Checks several common keys (`userFullName`, `displayName`, `firstName`/`lastName`) and
     * returns the first non-empty value found.
     */
    fun getUserFullName(ctx: Context): String? {
        val p = ctx.getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
        val candidates = listOf(
            p.getString("userFullName", null),
            p.getString("displayName", null),
            p.getString("userName", null)
        ).filterNotNull().map { it.trim() }.filter { it.isNotEmpty() }
        if (candidates.isNotEmpty()) return candidates.first()

        // Try to assemble from first/last name if available
        val first = p.getString("firstName", null)
        val last = p.getString("lastName", null)
        if (!first.isNullOrBlank() || !last.isNullOrBlank()) {
            return listOfNotNull(first?.trim(), last?.trim()).joinToString(" ")
        }

        return null
    }

    // --- debug panel preference helpers ---
    private const val KEY_DEBUG_PANEL_VISIBLE = "debug_panel_visible"

    fun isDebugPanelEnabled(ctx: Context): Boolean {
        val p = ctx.getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
        return p.getBoolean(KEY_DEBUG_PANEL_VISIBLE, false)
    }

    fun setDebugPanelEnabled(ctx: Context, enabled: Boolean) {
        val p = ctx.getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
        p.edit().putBoolean(KEY_DEBUG_PANEL_VISIBLE, enabled).apply()
    }

    // --- Conversation preview cache helpers (local optimistic UI) ---
    data class ConversationPreview(val conversationID: String?, val lastMessage: String?, val lastMessageAt: String?)

    private fun keyConv(partnerId: Long) = "preview_conv_${partnerId}"
    private fun keyMsg(partnerId: Long) = "preview_msg_${partnerId}"
    private fun keyAt(partnerId: Long) = "preview_at_${partnerId}"

    fun saveConversationPreview(ctx: Context, partnerId: Long, conversationID: String?, lastMessage: String?, lastMessageAt: String?) {
        try {
            val p = ctx.getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
            val e = p.edit()
            if (conversationID == null) e.remove(keyConv(partnerId)) else e.putString(keyConv(partnerId), conversationID)
            if (lastMessage == null) e.remove(keyMsg(partnerId)) else e.putString(keyMsg(partnerId), lastMessage)
            if (lastMessageAt == null) e.remove(keyAt(partnerId)) else e.putString(keyAt(partnerId), lastMessageAt)
            e.apply()
        } catch (_: Exception) { }
    }

    fun getConversationPreview(ctx: Context, partnerId: Long): ConversationPreview? {
        return try {
            val p = ctx.getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
            val conv = p.getString(keyConv(partnerId), null)
            val msg = p.getString(keyMsg(partnerId), null)
            val at = p.getString(keyAt(partnerId), null)
            if (conv == null && msg == null && at == null) null else ConversationPreview(conv, msg, at)
        } catch (_: Exception) { null }
    }

    fun clearConversationPreview(ctx: Context, partnerId: Long) {
        try {
            val p = ctx.getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
            p.edit().remove(keyConv(partnerId)).remove(keyMsg(partnerId)).remove(keyAt(partnerId)).apply()
        } catch (_: Exception) { }
    }
}

package com.example.capstone2.viewmodel

import androidx.lifecycle.*
import com.example.capstone2.data.api.ApiService
import com.example.capstone2.data.models.Message
import com.example.capstone2.data.models.SendMessageRequest
import kotlinx.coroutines.launch
import com.google.gson.Gson
import com.google.gson.JsonElement
import com.google.gson.JsonParser
import com.google.gson.reflect.TypeToken
import android.util.Log

class ChatViewModel(private val apiService: ApiService, private val currentUserId: Long) : ViewModel() {

    companion object {
        private const val TAG = "ChatViewModel"
    }

    private val _messages = MutableLiveData<List<Message>>(emptyList())
    val messages: LiveData<List<Message>> = _messages

    private val _error = MutableLiveData<String?>(null)
    val error: LiveData<String?> = _error

    // New: debug LiveData to show last request/response in UI when adb isn't available
    private val _debug = MutableLiveData<String?>(null)
    val debug: LiveData<String?> = _debug

    fun loadConversation(conversationID: String? = null, otherUserID: Long? = null) {
        viewModelScope.launch {
            try {
                val resp = apiService.getConversation(conversationID = conversationID, otherUserID = otherUserID)

                // Read both body and errorBody (reading them may consume them) and post debug immediately
                val responseBodyStr = try { resp.body()?.string() } catch (e: Exception) { null }
                val responseErrStr = try { resp.errorBody()?.string() } catch (e: Exception) { null }
                val respBodyShort = responseBodyStr ?: "<empty>"
                val respErrShort = responseErrStr ?: "<none>"
                _debug.postValue("Conversation HTTP ${resp.code()}: body=$respBodyShort err=$respErrShort")

                if (!resp.isSuccessful) {
                    _error.postValue("Failed to load conversation: ${resp.code()}")
                    return@launch
                }

                val bodyStr = responseBodyStr
                val bodyShort = bodyStr ?: "<empty>"
                if (Log.isLoggable(TAG, Log.DEBUG)) {
                    Log.d(TAG, "getConversation raw response: ${bodyStr?.take(4000)}")
                }
                _debug.postValue("Conversation response (userId=$currentUserId):\n$bodyShort")

                if (bodyStr.isNullOrBlank()) {
                    _messages.postValue(emptyList())
                    return@launch
                }

                try {
                    val gson = Gson()
                    val je = JsonParser.parseString(bodyStr)

                    // Helper: recursively find first array element in a JSON tree
                    fun findFirstArray(elem: JsonElement): JsonElement? {
                        if (elem == null) return null
                        if (elem.isJsonArray) return elem
                        if (elem.isJsonObject) {
                            val obj = elem.asJsonObject
                            val keys = listOf("messages", "data", "results", "items", "conversation", "body")
                            for (k in keys) {
                                if (obj.has(k)) {
                                    val child = obj.get(k)
                                    if (child.isJsonArray) return child
                                    val nested = findFirstArray(child)
                                    if (nested != null) return nested
                                }
                            }
                            val it = obj.entrySet().iterator()
                            while (it.hasNext()) {
                                val entry = it.next()
                                val found = findFirstArray(entry.value)
                                if (found != null) return found
                            }
                        }
                        return null
                    }

                    // Helper extractors (block-bodied so `return` is permitted inside)
                    fun JsonElement.asSafeString(): String? {
                        return try {
                            if (this.isJsonNull) null else this.asString
                        } catch (e: Exception) {
                            null
                        }
                    }

                    fun JsonElement.asSafeLong(): Long? {
                        return try {
                            if (this.isJsonNull) return null
                            val p = this.asJsonPrimitive
                            if (p.isNumber) return p.asLong
                            p.asString.toLongOrNull()
                        } catch (e: Exception) {
                            null
                        }
                    }

                    fun getStringFromObj(obj: com.google.gson.JsonObject, candidates: List<String>): String? {
                        for (k in candidates) {
                            if (obj.has(k) && !obj.get(k).isJsonNull) {
                                val v = try { obj.get(k) } catch (e: Exception) { null }
                                if (v != null) {
                                    val s = v.asSafeString()
                                    if (!s.isNullOrBlank()) return s
                                }
                            }
                        }
                        return null
                    }

                    fun getLongFromObj(obj: com.google.gson.JsonObject, candidates: List<String>): Long? {
                        for (k in candidates) {
                            if (obj.has(k) && !obj.get(k).isJsonNull) {
                                val v = try { obj.get(k) } catch (e: Exception) { null }
                                if (v != null) {
                                    val l = v.asSafeLong()
                                    if (l != null) return l
                                }
                            }
                        }
                        return null
                    }

                    // Parse array case
                    val firstArray = findFirstArray(je)
                    if (firstArray != null && firstArray.isJsonArray) {
                        val arr = firstArray.asJsonArray
                        val built = mutableListOf<Message>()

                        for (i in 0 until arr.size()) {
                            val elem = arr[i]
                            try {
                                if (elem.isJsonObject) {
                                    val obj = elem.asJsonObject
                                    val messageText = getStringFromObj(obj, listOf("message","body","text","content","messageText","msg","message_body"))
                                    val idVal = getLongFromObj(obj, listOf("messageID","id","msg_id"))
                                    val senderVal = getLongFromObj(obj, listOf("senderID","senderId","from","userID","userId"))
                                    val receiverVal = getLongFromObj(obj, listOf("receiverID","receiverId","to"))
                                    val created = getStringFromObj(obj, listOf("createdAt","timestamp","time","created_at"))
                                    val conv = getStringFromObj(obj, listOf("conversationID","conversationId","conversation"))
                                    val senderName = getStringFromObj(obj, listOf("senderName","sender_name","name","displayName","display_name"))
                                    val receiverName = getStringFromObj(obj, listOf("receiverName","receiver_name","receiver","toName","to","receiverDisplayName","receiver_display_name"))

                                    val msg = Message(
                                        id = idVal,
                                        senderID = senderVal ?: -1L,
                                        receiverID = receiverVal ?: -1L,
                                        message = messageText ?: "",
                                        timestamp = created,
                                        conversationID = conv,
                                        senderName = senderName,
                                        receiverName = receiverName
                                    )
                                    built.add(msg)
                                } else {
                                    // not an object: attempt to parse with Gson fallback
                                    try {
                                        val parsed = gson.fromJson(elem, Message::class.java)
                                        if (parsed != null) built.add(parsed)
                                    } catch (_: Exception) {
                                        // ignore
                                    }
                                }
                            } catch (e: Exception) {
                                // ignore element-level errors
                            }
                        }

                        _messages.postValue(built)
                        return@launch
                    }

                    // Single object heuristics
                    if (je.isJsonObject) {
                        val obj = je.asJsonObject
                        if (obj.has("senderID") || obj.has("message") || obj.has("id") || obj.has("conversationID")) {
                            val single = try { gson.fromJson(obj, Message::class.java) } catch (e: Exception) { null }
                            if (single != null) {
                                _messages.postValue(listOf(single))
                                return@launch
                            }
                        }
                    }

                    // Final fallback: try parse the body as List<Message>
                    try {
                        val messagesArray = try { gson.fromJson(bodyStr, Array<Message>::class.java) } catch (e: Exception) { null }
                        if (messagesArray != null) {
                            _messages.postValue(messagesArray.toList())
                            return@launch
                        }
                    } catch (e: Exception) {
                        Log.e(TAG, "Failed to parse body as messages array")
                        val emsg = e.message ?: "<null>"
                        if (Log.isLoggable(TAG, Log.DEBUG)) {
                            Log.d(TAG, "Exception while parsing list: " + emsg)
                            Log.d(TAG, "Raw response (full): " + (bodyStr ?: "<empty>"))
                        }
                        _messages.postValue(emptyList())
                        return@launch
                    }

                } catch (e: Exception) {
                    Log.e(TAG, "Failed to parse messages JSON")
                    if (Log.isLoggable(TAG, Log.DEBUG)) {
                        Log.d(TAG, "Exception while parsing messages JSON: " + (e.message ?: "<null>"))
                        Log.d(TAG, "Raw response (full): " + (bodyStr ?: "<empty>"))
                    }
                    _error.postValue("Failed to parse messages: " + (e.message ?: ""))
                    return@launch
                }

            } catch (e: Exception) {
                _error.postValue(e.message)
            }
        }
    }

    fun sendMessage(receiverID: Long, text: String, conversationID: String? = null) {
        viewModelScope.launch {
            try {
                val req = SendMessageRequest(receiverID = receiverID, message = text, conversationID = conversationID)
                // Debug: outgoing JSON
                try {
                    val gson = Gson()
                    val outJson = gson.toJson(req)
                    if (Log.isLoggable(TAG, Log.DEBUG)) {
                        Log.d(TAG, "sendMessage request JSON: $outJson")
                    }
                    _debug.postValue("Outgoing request:\n$outJson")
                } catch (e: Exception) {}

                // Primary send
                // The project's ApiService exposes `sendMessageRaw` which accepts a Map payload.
                // Build a raw payload map from the request and use that for the primary send.
                val rawPayload = mutableMapOf<String, Any>("message" to text, "receiverID" to receiverID)
                conversationID?.let { rawPayload["conversationID"] = it }
                val resp = apiService.sendMessageRaw(rawPayload)
                if (resp.isSuccessful) {
                    val bodyStr = try { resp.body()?.string() } catch (e: Exception) { null }
                    if (Log.isLoggable(TAG, Log.DEBUG)) {
                        Log.d(TAG, "sendMessage response raw: ${bodyStr?.take(2000)}")
                    }
                    if (!bodyStr.isNullOrBlank()) {
                         try {
                             val gson = Gson()
                             val je = JsonParser.parseString(bodyStr)
                             var parsedMessage: Message? = null
                             if (je.isJsonObject) {
                                 val obj = je.asJsonObject
                                 if (obj.has("data")) {
                                     try { parsedMessage = gson.fromJson(obj.get("data"), Message::class.java) } catch (e: Exception) {}
                                 }
                                 if (parsedMessage == null) {
                                     try { parsedMessage = gson.fromJson(obj, Message::class.java) } catch (e: Exception) {}
                                 }
                             } else if (je.isJsonArray) {
                                 try {
                                     val arr = je.asJsonArray
                                     if (arr.size() > 0) parsedMessage = gson.fromJson(arr[0], Message::class.java)
                                 } catch (e: Exception) {}
                             }

                             if (parsedMessage != null) {
                                 val current = _messages.value?.toMutableList() ?: mutableListOf()
                                 current.add(parsedMessage)
                                 _messages.postValue(current)
                                 _debug.postValue("Sent OK. Created message id=${parsedMessage.id}")
                                 // refresh authoritative state
                                 loadConversation(conversationID = conversationID, otherUserID = receiverID)
                             } else {
                                 // fallback: refresh conversation to pull the message
                                 _debug.postValue((_debug.value ?: "") + "\nWarning: couldn't parse created message from server response")
                                 loadConversation(conversationID = conversationID, otherUserID = receiverID)
                             }
                         } catch (e: Exception) {
                            Log.e(TAG, "Failed to parse sendMessage response: " + (e.message ?: "<null>"))
                             _debug.postValue((_debug.value ?: "") + "\nFailed to parse send response: " + (e.message ?: ""))
                             loadConversation(conversationID = conversationID, otherUserID = receiverID)
                         }
                     } else {
                         // empty body, refresh
                         loadConversation(conversationID = conversationID, otherUserID = receiverID)
                     }
                 } else {
                     // Non-successful primary response
                     val errBody = try { resp.errorBody()?.string() } catch (e: Exception) { null }
                     Log.e(TAG, "sendMessage failed with HTTP ${resp.code()} body=${errBody}")
                     val errShort = errBody ?: "<no body>"
                     _debug.postValue("Server error: HTTP ${resp.code()}\n$errShort")

                     if (resp.code() == 422) {
                         // validation: try a raw retry with camelCase keys
                         val rawMap = mutableMapOf<String, Any>("message" to text, "receiverID" to receiverID)
                         conversationID?.let { rawMap["conversationID"] = it }
                        if (Log.isLoggable(TAG, Log.DEBUG)) {
                             Log.d(TAG, "Attempting raw send retry with body: $rawMap")
                         }
                         _debug.postValue((_debug.value ?: "") + "\nRetrying with raw body: $rawMap")

                         try {
                             val retryResp = apiService.sendMessageRaw(rawMap)
                             if (retryResp.isSuccessful) {
                                val bodyStr = try { retryResp.body()?.string() } catch (e: Exception) { null }
                                if (Log.isLoggable(TAG, Log.DEBUG)) {
                                    Log.d(TAG, "retry raw response: ${bodyStr?.take(2000)}")
                                }
                                 _debug.postValue((_debug.value ?: "") + "\nRetry response: " + (bodyStr ?: "<empty>"))

                                 if (!bodyStr.isNullOrBlank()) {
                                     try {
                                         val gson = Gson()
                                         val je = JsonParser.parseString(bodyStr)
                                         var maybe: Message? = null
                                         if (je.isJsonObject) {
                                             val obj = je.asJsonObject
                                             if (obj.has("data")) {
                                                 try { maybe = gson.fromJson(obj.get("data"), Message::class.java) } catch (e: Exception) {}
                                             }
                                             if (maybe == null) {
                                                 try { maybe = gson.fromJson(obj, Message::class.java) } catch (e: Exception) {}
                                             }
                                         } else if (je.isJsonArray) {
                                             try { val arr = je.asJsonArray; if (arr.size() > 0) maybe = gson.fromJson(arr[0], Message::class.java) } catch (e: Exception) {}
                                         }

                                         if (maybe != null) {
                                             val current = _messages.value?.toMutableList() ?: mutableListOf()
                                             current.add(maybe)
                                             _messages.postValue(current)
                                             _debug.postValue((_debug.value ?: "") + "\nRetry parsed message id=${maybe.id}")
                                             loadConversation(conversationID = conversationID, otherUserID = receiverID)
                                             return@launch
                                         } else {
                                             // couldn't parse but HTTP OK: refresh
                                             loadConversation(conversationID = conversationID, otherUserID = receiverID)
                                         }
                                     } catch (e: Exception) {
                                         Log.e(TAG, "retry parse exception", e)
                                         // fallthrough to error handling below
                                     }
                                 } else {
                                     // empty retry body: refresh
                                     loadConversation(conversationID = conversationID, otherUserID = receiverID)
                                 }
                             } else {
                                val retryErr = try { retryResp.errorBody()?.string() } catch (e: Exception) { null }
                                Log.e(TAG, "raw retry failed HTTP ${retryResp.code()} body=${retryErr}")
                                 val userVisible = buildString {
                                     append("Failed to send message: ${resp.code()}")
                                     if (!errBody.isNullOrBlank()) {
                                         append(" - ")
                                         append(if (errBody.length > 1000) errBody.take(1000) + "..." else errBody)
                                     }
                                     if (!retryErr.isNullOrBlank()) {
                                         append(" | Retry: ")
                                         append(if (retryErr.length > 1000) retryErr.take(1000) + "..." else retryErr)
                                     }
                                 }
                                 _error.postValue(userVisible)
                                 val retryErrShort = retryErr ?: "<no body>"
                                 _debug.postValue((_debug.value ?: "") + "\nRetry failed: $retryErrShort")
                                 return@launch
                             }
                         } catch (e: Exception) {
                            Log.e(TAG, "raw retry exception: " + (e.message ?: "<null>"))
                             _error.postValue(errBody ?: e.message ?: "Failed to send message")
                             _debug.postValue((_debug.value ?: "") + "\nRetry exception: " + (e.message ?: ""))
                             return@launch
                         }
                     } else {
                         // For 500/internal errors, show server status and let maintainer fix it
                         _error.postValue("Server returned HTTP ${resp.code()}")
                         return@launch
                     }
                 }
             } catch (e: Exception) {
                Log.e(TAG, "sendMessage exception: " + (e.message ?: "<null>"))
                 _error.postValue(e.message)
                 _debug.postValue((_debug.value ?: "") + "\nException: " + (e.message ?: ""))
             }
         }
     }
 }

package com.example.capstone2.viewmodel

import androidx.lifecycle.*
import com.example.capstone2.data.api.ApiService
import com.example.capstone2.data.models.Message
import com.example.capstone2.data.models.SendMessageRequest
import kotlinx.coroutines.launch
import com.google.gson.Gson
import com.google.gson.JsonElement
import com.google.gson.JsonParser
import com.google.gson.reflect.TypeToken
import android.util.Log

class ChatViewModel(private val apiService: ApiService, private val currentUserId: Long) : ViewModel() {

    companion object {
        private const val TAG = "ChatViewModel"
    }

    private val _messages = MutableLiveData<List<Message>>(emptyList())
    val messages: LiveData<List<Message>> = _messages

    private val _error = MutableLiveData<String?>(null)
    val error: LiveData<String?> = _error

    fun loadConversation(conversationID: String? = null, otherUserID: Long? = null) {
        viewModelScope.launch {
            try {
                val resp = apiService.getConversation(conversationID = conversationID, otherUserID = otherUserID)

                // Read both body and errorBody (reading them may consume them)
                val responseBodyStr = try { resp.body()?.string() } catch (e: Exception) { null }
                val responseErrStr = try { resp.errorBody()?.string() } catch (e: Exception) { null }
                val respBodyShort = responseBodyStr ?: "<empty>"
                val respErrShort = responseErrStr ?: "<none>"
                if (Log.isLoggable(TAG, Log.DEBUG)) {
                    Log.d(TAG, "getConversation raw response: ${responseBodyStr?.take(4000)}")
                }

                if (!resp.isSuccessful) {
                    _error.postValue("Failed to load conversation: ${resp.code()}")
                    return@launch
                }

                val bodyStr = responseBodyStr
                val bodyShort = bodyStr ?: "<empty>"
                if (Log.isLoggable(TAG, Log.DEBUG)) {
                    Log.d(TAG, "getConversation raw response: ${bodyStr?.take(4000)}")
                }

                if (bodyStr.isNullOrBlank()) {
                    _messages.postValue(emptyList())
                    return@launch
                }

                try {
                    val gson = Gson()
                    val je = JsonParser.parseString(bodyStr)

                    // Helper: recursively find first array element in a JSON tree
                    fun findFirstArray(elem: JsonElement): JsonElement? {
                        if (elem == null) return null
                        if (elem.isJsonArray) return elem
                        if (elem.isJsonObject) {
                            val obj = elem.asJsonObject
                            val keys = listOf("messages", "data", "results", "items", "conversation", "body")
                            for (k in keys) {
                                if (obj.has(k)) {
                                    val child = obj.get(k)
                                    if (child.isJsonArray) return child
                                    val nested = findFirstArray(child)
                                    if (nested != null) return nested
                                }
                            }
                            val it = obj.entrySet().iterator()
                            while (it.hasNext()) {
                                val entry = it.next()
                                val found = findFirstArray(entry.value)
                                if (found != null) return found
                            }
                        }
                        return null
                    }

                    // Helper extractors (block-bodied so `return` is permitted inside)
                    fun JsonElement.asSafeString(): String? {
                        return try {
                            if (this.isJsonNull) null else this.asString
                        } catch (e: Exception) {
                            null
                        }
                    }

                    fun JsonElement.asSafeLong(): Long? {
                        return try {
                            if (this.isJsonNull) return null
                            val p = this.asJsonPrimitive
                            if (p.isNumber) return p.asLong
                            p.asString.toLongOrNull()
                        } catch (e: Exception) {
                            null
                        }
                    }

                    // Helpers to extract string/long candidates from a JSON object
                    fun getStringFromObj(obj: com.google.gson.JsonObject, candidates: List<String>): String? {
                        for (k in candidates) {
                            if (obj.has(k) && !obj.get(k).isJsonNull) {
                                try {
                                    val v = obj.get(k)
                                    val s = v.asSafeString()
                                    if (!s.isNullOrBlank()) return s
                                } catch (_: Exception) { }
                            }
                        }
                        return null
                    }

                    fun getLongFromObj(obj: com.google.gson.JsonObject, candidates: List<String>): Long? {
                        for (k in candidates) {
                            if (obj.has(k) && !obj.get(k).isJsonNull) {
                                try {
                                    val v = obj.get(k)
                                    val l = v.asSafeLong()
                                    if (l != null) return l
                                } catch (_: Exception) { }
                            }
                        }
                        return null
                    }

                    // Helper: parse timestamp string to epoch millis (tries plain long, ISO instant, and common formats)
                    fun parseTimeToMillis(t: String?): Long? {
                        if (t == null) return null
                        try {
                            val asLong = t.toLongOrNull()
                            if (asLong != null) return if (asLong < 1000000000000L) asLong * 1000L else asLong
                        } catch (_: Exception) { }
                        try { return java.time.Instant.parse(t).toEpochMilli() } catch (_: Exception) { }
                        try {
                            val fmt = java.time.format.DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss")
                            val ldt = java.time.LocalDateTime.parse(t, fmt)
                            return ldt.atZone(java.time.ZoneId.systemDefault()).toInstant().toEpochMilli()
                        } catch (_: Exception) { }
                        return null
                    }

                    // Parse array case
                    val firstArray = findFirstArray(je)
                    if (firstArray != null && firstArray.isJsonArray) {
                        val arr = firstArray.asJsonArray
                        val built = mutableListOf<Message>()

                        for (i in 0 until arr.size()) {
                            val elem = arr[i]
                            try {
                                if (elem.isJsonObject) {
                                    val obj = elem.asJsonObject
                                    val messageText = getStringFromObj(obj, listOf("message","body","text","content","messageText","msg","message_body"))
                                    val idVal = getLongFromObj(obj, listOf("messageID","id","msg_id"))
                                    val senderVal = getLongFromObj(obj, listOf("senderID","senderId","from","userID","userId"))
                                    val receiverVal = getLongFromObj(obj, listOf("receiverID","receiverId","to"))
                                    val created = getStringFromObj(obj, listOf("createdAt","timestamp","time","created_at"))
                                    val conv = getStringFromObj(obj, listOf("conversationID","conversationId","conversation"))
                                    val senderName = getStringFromObj(obj, listOf("senderName","sender_name","name","displayName","display_name"))
                                    val receiverName = getStringFromObj(obj, listOf("receiverName","receiver_name","receiver","toName","to","receiverDisplayName","receiver_display_name"))

                                    val msg = Message(
                                        id = idVal,
                                        senderID = senderVal ?: -1L,
                                        receiverID = receiverVal ?: -1L,
                                        message = messageText ?: "",
                                        timestamp = created,
                                        conversationID = conv,
                                        senderName = senderName,
                                        receiverName = receiverName
                                    )
                                    built.add(msg)
                                } else {
                                    // not an object: attempt to parse with Gson fallback
                                    try {
                                        val parsed = gson.fromJson(elem, Message::class.java)
                                        if (parsed != null) built.add(parsed)
                                    } catch (_: Exception) {
                                        // ignore
                                    }
                                }
                            } catch (e: Exception) {
                                // ignore element-level errors
                            }
                        }

                        // Sort messages oldest-first (so newest is last) by parsed timestamp when possible
                        try {
                            built.sortBy { parseTimeToMillis(it.timestamp) ?: Long.MAX_VALUE }
                        } catch (_: Exception) { }

                        _messages.postValue(built)
                        return@launch
                    }

                    // Single object heuristics
                    if (je.isJsonObject) {
                        val obj = je.asJsonObject
                        if (obj.has("senderID") || obj.has("message") || obj.has("id") || obj.has("conversationID")) {
                            val single = try { gson.fromJson(obj, Message::class.java) } catch (e: Exception) { null }
                            if (single != null) {
                                _messages.postValue(listOf(single))
                                return@launch
                            }
                        }
                    }

                    // Final fallback: try parse the body as List<Message>
                    try {
                        val messagesArray = try { gson.fromJson(bodyStr, Array<Message>::class.java) } catch (e: Exception) { null }
                        if (messagesArray != null) {
                            // Ensure oldest-first ordering
                            val list = messagesArray.toMutableList()
                            try { list.sortBy { parseTimeToMillis(it.timestamp) ?: Long.MAX_VALUE } } catch (_: Exception) {}
                            _messages.postValue(list)
                            return@launch
                        }
                    } catch (e: Exception) {
                        Log.e(TAG, "Failed to parse body as messages array")
                        val emsg = e.message ?: "<null>"
                        if (Log.isLoggable(TAG, Log.DEBUG)) {
                            Log.d(TAG, "Exception while parsing list: " + emsg)
                            Log.d(TAG, "Raw response (full): " + (bodyStr ?: "<empty>"))
                        }
                        _messages.postValue(emptyList())
                        return@launch
                    }

                } catch (e: Exception) {
                    Log.e(TAG, "Failed to parse messages JSON")
                    if (Log.isLoggable(TAG, Log.DEBUG)) {
                        Log.d(TAG, "Exception while parsing messages JSON: " + (e.message ?: "<null>"))
                        Log.d(TAG, "Raw response (full): " + (bodyStr ?: "<empty>"))
                    }
                    _error.postValue("Failed to parse messages: " + (e.message ?: ""))
                    return@launch
                }

            } catch (e: Exception) {
                _error.postValue(e.message)
            }
        }
    }

    fun sendMessage(receiverID: Long, text: String, conversationID: String? = null) {
        viewModelScope.launch {
            try {
                val req = SendMessageRequest(receiverID = receiverID, message = text, conversationID = conversationID)
                // Debug: outgoing JSON
                try {
                    val gson = Gson()
                    val outJson = gson.toJson(req)
                    if (Log.isLoggable(TAG, Log.DEBUG)) {
                        Log.d(TAG, "sendMessage request JSON: $outJson")
                    }
                } catch (e: Exception) {}

                // Primary send
                // The project's ApiService exposes `sendMessageRaw` which accepts a Map payload.
                // Build a raw payload map from the request and use that for the primary send.
                val rawPayload = mutableMapOf<String, Any>("message" to text, "receiverID" to receiverID)
                conversationID?.let { rawPayload["conversationID"] = it }
                val resp = apiService.sendMessageRaw(rawPayload)
                if (resp.isSuccessful) {
                    val bodyStr = try { resp.body()?.string() } catch (e: Exception) { null }
                    if (Log.isLoggable(TAG, Log.DEBUG)) {
                        Log.d(TAG, "sendMessage response raw: ${bodyStr?.take(2000)}")
                    }
                    if (!bodyStr.isNullOrBlank()) {
                         try {
                             val gson = Gson()
                             val je = JsonParser.parseString(bodyStr)
                             var parsedMessage: Message? = null
                             if (je.isJsonObject) {
                                 val obj = je.asJsonObject
                                 if (obj.has("data")) {
                                     try { parsedMessage = gson.fromJson(obj.get("data"), Message::class.java) } catch (e: Exception) {}
                                 }
                                 if (parsedMessage == null) {
                                     try { parsedMessage = gson.fromJson(obj, Message::class.java) } catch (e: Exception) {}
                                 }
                             } else if (je.isJsonArray) {
                                 try {
                                     val arr = je.asJsonArray
                                     if (arr.size() > 0) parsedMessage = gson.fromJson(arr[0], Message::class.java)
                                 } catch (e: Exception) {}
                             }

                             if (parsedMessage != null) {
                                 val current = _messages.value?.toMutableList() ?: mutableListOf()
                                 current.add(parsedMessage)
                                 _messages.postValue(current)
                                 // refresh authoritative state
                                 loadConversation(conversationID = conversationID, otherUserID = receiverID)
                             } else {
                                 // fallback: refresh conversation to pull the message
                                 loadConversation(conversationID = conversationID, otherUserID = receiverID)
                             }
                         } catch (e: Exception) {
                            Log.e(TAG, "Failed to parse sendMessage response: " + (e.message ?: "<null>"))
                             loadConversation(conversationID = conversationID, otherUserID = receiverID)
                         }
                     } else {
                         // empty body, refresh
                         loadConversation(conversationID = conversationID, otherUserID = receiverID)
                     }
                 } else {
                     // Non-successful primary response
                     val errBody = try { resp.errorBody()?.string() } catch (e: Exception) { null }
                     Log.e(TAG, "sendMessage failed with HTTP ${resp.code()} body=${errBody}")

                     if (resp.code() == 422) {
                         // validation: try a raw retry with camelCase keys
                         val rawMap = mutableMapOf<String, Any>("message" to text, "receiverID" to receiverID)
                         conversationID?.let { rawMap["conversationID"] = it }
                        if (Log.isLoggable(TAG, Log.DEBUG)) {
                             Log.d(TAG, "Attempting raw send retry with body: $rawMap")
                         }

                         try {
                             val retryResp = apiService.sendMessageRaw(rawMap)
                             if (retryResp.isSuccessful) {
                                val bodyStr = try { retryResp.body()?.string() } catch (e: Exception) { null }
                                if (Log.isLoggable(TAG, Log.DEBUG)) {
                                    Log.d(TAG, "retry raw response: ${bodyStr?.take(2000)}")
                                }

                                 if (!bodyStr.isNullOrBlank()) {
                                     try {
                                         val gson = Gson()
                                         val je = JsonParser.parseString(bodyStr)
                                         var maybe: Message? = null
                                         if (je.isJsonObject) {
                                             val obj = je.asJsonObject
                                             if (obj.has("data")) {
                                                 try { maybe = gson.fromJson(obj.get("data"), Message::class.java) } catch (e: Exception) {}
                                             }
                                             if (maybe == null) {
                                                 try { maybe = gson.fromJson(obj, Message::class.java) } catch (e: Exception) {}
                                             }
                                         } else if (je.isJsonArray) {
                                             try { val arr = je.asJsonArray; if (arr.size() > 0) maybe = gson.fromJson(arr[0], Message::class.java) } catch (e: Exception) {}
                                         }

                                         if (maybe != null) {
                                             val current = _messages.value?.toMutableList() ?: mutableListOf()
                                             current.add(maybe)
                                             _messages.postValue(current)
                                             loadConversation(conversationID = conversationID, otherUserID = receiverID)
                                             return@launch
                                         } else {
                                             // couldn't parse but HTTP OK: refresh
                                             loadConversation(conversationID = conversationID, otherUserID = receiverID)
                                         }
                                     } catch (e: Exception) {
                                         Log.e(TAG, "retry parse exception", e)
                                         // fallthrough to error handling below
                                     }
                                 } else {
                                     // empty retry body: refresh
                                     loadConversation(conversationID = conversationID, otherUserID = receiverID)
                                 }
                             } else {
                                val retryErr = try { retryResp.errorBody()?.string() } catch (e: Exception) { null }
                                Log.e(TAG, "raw retry failed HTTP ${retryResp.code()} body=${retryErr}")
                                 val userVisible = buildString {
                                     append("Failed to send message: ${resp.code()}")
                                     if (!errBody.isNullOrBlank()) {
                                         append(" - ")
                                         append(if (errBody.length > 1000) errBody.take(1000) + "..." else errBody)
                                     }
                                     if (!retryErr.isNullOrBlank()) {
                                         append(" | Retry: ")
                                         append(if (retryErr.length > 1000) retryErr.take(1000) + "..." else retryErr)
                                     }
                                 }
                                 _error.postValue(userVisible)
                                 return@launch
                             }
                         } catch (e: Exception) {
                            Log.e(TAG, "raw retry exception: " + (e.message ?: "<null>"))
                             _error.postValue(errBody ?: e.message ?: "Failed to send message")
                             return@launch
                         }
                     } else {
                         // For 500/internal errors, show server status and let maintainer fix it
                         _error.postValue("Server returned HTTP ${resp.code()}")
                         return@launch
                     }
                 }
             } catch (e: Exception) {
                Log.e(TAG, "sendMessage exception: " + (e.message ?: "<null>"))
                 _error.postValue(e.message)
             }
         }
     }
 }
